create table fournisseurs (id int primary key, nom_entreprise varchar(256) NOT NULL, nom_representant varchar(256), description_fournisseur varchar(256), quartier varchar(256), ville varchar(256), province_etat varchar(256), pays varchar(256), boite_postale varchar(256), siege_social varchar(256), email varchar(256), numero_telephone_1 varchar(256), numero_telephone_2 varchar(256), numero_contribuable varchar(256));
