create table paiements (id int auto_increment primary key, date_paiement date NOT NULL, heure_paiement time NOT NULL, nom_entreprise varchar(256) NOT NULL, nom_encaisseur varchar(256) NOT NULL, montant_paye double NOT NULL, compte_client double NOT NULL, type_de_paiement int(12) NOT NULL, reference varchar(256) NOT NULL, notes varchar(256), intitule_du_compte_bancaire varchar(256), reference_recu_paiement_client varchar(256));
